import React from 'react';

import {
    GroupContainer,
    FormInputContainer,
    FormInputLabel
  } from './form-input.styles';

//FUCNTIONAL COMPONENT

const FormInput = ({ handleChange, label, ...otherProps }) => (
    <GroupContainer>
        <FormInputContainer
            onChange={handleChange} {...otherProps}
        />

        {
            label ? (

                <FormInputLabel 
                    className={`${
                        otherProps.value.length ? 'shrink' : ''
                    } form-input-label`}
                >
                    {label}
                </FormInputLabel>

            ) : null
        }
    </GroupContainer>
);

export default FormInput;
