import React from 'react';
import StripeCheckout from 'react-stripe-checkout';
import axios from 'axios';

const StripeCheckoutButton = ({price}) => {
    const priceForStripe = price * 100;
    const publishableKey = 'pk_test_GjOCB4XD8Y9yEkEfRltay2SM00nzfFdfUw';


    const onToken = token => {
        axios({
            url: 'payment',
            method: 'post',
            data: {
                amount: priceForStripe,
                token
            }
        }).then(response => {
            alert('Payment Successful')

        }).catch(error => {
            console.log('Payment Error: ', JSON.pasrse(error));
            alert(
                'There was an issue with your payment. Please make sure you use the provident Credit Card information.'
            );;
        });
    };

    return (
        <StripeCheckout
            label = 'Pay Now'
            name='Ribbon Clothing Ltd.'
            billingAddress
            shippingAddress
            image='https://svgshare.com/i/CUz.svg'
            description={`Your total is $${price}`}
            amount={priceForStripe}
            panelLabel='Pay Now'
            token={onToken}
            stripeKey={publishableKey}

        />
    );
};


export default StripeCheckoutButton;
